sys_script_client.script:
function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   	g_form.clearOptions('u_what_needed');


   var options = [];

   if (newValue === 'hr') {
      options = ['Human Resources 1', 'Human Resources 2', 'Other'];
   } 
   if (newValue === 'facilities') {
      options = ['Facilities 1', 'Facilities 2', 'Other'];
   } 
   if (newValue === 'legal') {
      options = ['Legal 1', 'Legal 2', 'Other'];
   }

   options.forEach(function(option) {
      g_form.addOption('u_what_needed', option, option);
   });

   if (!g_form.isNewRecord()) {
      var previousWhatNeeded = g_form.getValue('u_what_needed'); 
      g_form.setValue('u_what_needed', previousWhatNeeded); 
   }
}
